<script>
import { Options } from '../../main.js'

function randId () {
  return Math.random().toString(36).substr(2, 10)
}

export default {
  isWidget: true,
  name: 'eg-code-block',
  props: {
    id: {default: () => randId()},
    id2: {default: () => randId()},
    id3: {default: () => randId()},
